  `userid` varchar(20) NOT NULL,
  `course` int(11) NOT NULL,
  `class` int(11) NOT NULL,
  `team` int(11) DEFAULT NULL,
  PRIMARY KEY (`userid`,`course`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

